import {Vector} from '@kit.ArkTS';

export function removeDuplicates<T>(vector: Vector<T>): void {
  const seen = new Set<T>();
  let i = 0;
  while (i < vector.length) {
    const element = vector.getIndexOf(i);
    if (seen.has(element)) {
      vector.removeByIndex(i);
    } else {
      seen.add(element);
      i++;
    }
  }
}

