import {util} from '@kit.ArkTS';
type TextDecoder = util.TextDecoder;

export function decodeStream(chunks: Uint8Array[]): string {
  const decoder = new TextDecoder('utf-16', {fatal: true});
  let result = '';
  for (const chunk of chunks) {
    result += decoder.decodeWithStream(chunk);
  }
  return result;
}

