import {List} from '@kit.ArkTS';

export function removeDuplicates<T>(list: List<T>): void {
  const seen = new Set<T>();
  let i = 0;
  while (i < list.length) {
    const element = list.get(i);
    if (seen.has(element)) {
      list.remove(element);
    } else {
      seen.add(element);
      i++;
    }
  }
}

