import {stream} from '@kit.ArkTS';
type Transform = stream.Transform;

export function createAndChainTransform(): Transform {
  const transform = new stream.Transform();

  transform.doTransform = (chunk: string, encoding: string, callback: () => void): void => {
    const transformedChunk = chunk.toUpperCase();
    transform.push(transformedChunk);
    callback();
  };

  transform.doFlush = (callback: () => void): void => {
    transform.push('FLUSHED');
    callback();
  };

  return transform;
}

import {stream} from '@kit.ArkTS';
type Readable = stream.Readable;

export function initializeAndReadStream(stream: Readable, size: number, callback: (error?: Error) => void): void {
  stream.doInitialize(() => {
    if (stream.isPaused()) {
      stream.resume();
    }
    try {
      stream.doRead(size);
      callback();
    } catch (error) {
      callback(error as Error);
    }
  });
}

