export function isValidUrl(urlObj: url.URL): boolean {
  const urlString = urlObj.toString();
  const urlRegex = /^(https?:\/\/)?([\da-z\.-]+)\.([a-z\.]{2,6})([\/\w \.-]*)*\/?$/;
  return urlRegex.test(urlString);
}

import {TreeMap} from '@kit.ArkTS';

export function findKeyByValue<K, V>(treeMap: TreeMap<K, V>, value: V): K | undefined {
  if (treeMap.hasValue(value)) {
    for (let key of treeMap.keys()) {
      if (treeMap.get(key) === value) {
        return key;
      }
    }
  }
  return undefined;
}

