      set2.add(true);
      removeCommonElements(set1, set2);
      expect(set1.size).assertEqual(0);
    });
    it('should_remove_all_elements_when_all_are_common', 0, () => {
      const set1 = new HashSet<number>();
      set1.add(1);
      set1.add(2);
      const set2 = new HashSet<number>();
      set2.add(1);
      set2.add(2);
      removeCommonElements(set1, set2);
      expect(set1.size).assertEqual(0);
    });
  });
}

import {LinkedList} from '@kit.ArkTS';

export function appendArrayToLinkedList<T>(list: LinkedList<T>, array: T[]): void {
