import {xml} from '@kit.ArkTS';
type EventType = xml.EventType;

export function isValidEventType(eventType: EventType): boolean {
  const validEventTypes: EventType[] = [
    xml.EventType.ELEMENT_START,
    xml.EventType.ELEMENT_END,
    xml.EventType.TEXT,
    xml.EventType.CDATA,
    xml.EventType.COMMENT,
    xml.EventType.DOCUMENT_START,
    xml.EventType.DOCUMENT_END
  ];

  return validEventTypes.includes(eventType);
}

import {xml} from '@kit.ArkTS';
type XmlPullParser = xml.XmlPullParser;

export function extractRootElementName(xmlString: string): string {
  const encoder = new TextEncoder();
  const arrayBuffer = encoder.encode(xmlString);
  const parser: XmlPullParser = new xml.XmlPullParser(arrayBuffer);
  const parseOptions: xml.ParseOptions = {
    supportDoctype: true,
    ignoreNameSpace: true
  };
  parser.parse(parseOptions);

  const parseResult = parser.parseXml(parseOptions);
  return parseResult.getName();
}

