import {PlainArray} from '@kit.ArkTS';

export function getKeysByValue(plainArray: PlainArray<number>, targetValue: number): number[] {
  const keys: number[] = [];

  plainArray.forEach((value: number, key: number) => {
    if (value === targetValue) {
      keys.push(key);
    }
  });

  return keys;
}

