import {Stack} from '@kit.ArkTS';

export function findPositionInStack<T>(stack: Stack<T>, value: T): number {
  if (stack.isEmpty()) {
    return -1;
  }
  const position = stack.locate(value);
  return position === -1 ? -1 : position + 1;
}

